#!/bin/bash
parallel-ssh -v -h ~/caelum_tmp -t 0 -p 50 -i 'PROCID=`./falkirk-experiments/scripts/get_falkirk_proc_id.sh` ; cd falkirk/Naiad/ ; mono-sgen ./FaultToleranceExamples/bin/Release/FaultToleranceExamples.exe complex-ft-workflow -mwc 6 -log /tmp/falkirk/ -p $PROCID -n 25 -t 1 -slowBase 1 -slowRange 5 -ccBase 6 -ccRange 15 -fpBase 21 -fpRange 4 -numberOfKeys 10000 -fastBatchSize 1 -fastSleepTime 1000 -ccBatchTime 20000 -slowBatchTime 60000 -htBatchSize 512 -htSleepTime 1000 -htInitialBatches 20 -nofailures -stoptheworldft -stoptheworldfrequency 10000 -h @/home/${USER}/machines &> caelum$PROCID.log'

